import React, {useState, useEffect} from 'react'


function Game() {
    const STARTING_TIME = 5
    const [text, setText] = useState('')
    const [timeReamining, setTimeRemaining] = useState(STARTING_TIME)
    const [isTimeRunning, setIsTimeRunning] = useState(false)
    const [wordNum, setWordNum] = useState(0)

    function handleChange(e) {
        e.preventDefault()
        const {value} = e.target
        setText(value)
    }

    function wordCount(text) {
        let words = text.trim().split(' ')
        return words.filter(word => word !== '').length

    }

    function startClock() {
        setIsTimeRunning(true)
        setTimeRemaining(STARTING_TIME)
        setText('')
    }

    function endGame() {
        setIsTimeRunning(false)
        setWordNum(wordCount(text))
    }


    useEffect(() => {
        if(isTimeRunning && timeReamining > 0)
        {setTimeout(() => {
           setTimeRemaining(time => time - 1)
        }, 1000)}else if(timeReamining === 0){
            endGame()
        }
    }, [timeReamining, isTimeRunning])

    console.log(isTimeRunning)


    return(
        <div>
            <h1>How fast can you type?</h1>
            <textarea
                value={text}
                onChange={handleChange}
                disabled={isTimeRunning}
            />
            <h4>Time remaining: {timeReamining}</h4>
            <button 
            onClick={startClock}
            disabled={isTimeRunning}
            >Start</button>
            <h1>Word count: {wordNum} </h1>

        </div>
    )
}

export default Game
